package exerciciosjavadois;

public class Ex12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		        int numero = 1;

		        System.out.println("Números ímpares de 1 a 50:");

		        while (numero <= 50) {
		            if (numero % 2 != 0) {
		                System.out.println(numero);
		            }
		            numero++;
		}

	}

}
