package exerciciosjavadois;

public class Ex18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		        int numero = 2;

		        System.out.println("Números pares de 1 a 50:");

		        do {
		            System.out.println(numero);
		            numero += 2;
		        } while (numero <= 50);
	}

}
