package exerciciosjavadois;

public class Ex27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		        int numero = 1;

		        System.out.println("Números ímpares de 1 a 50:");

		        do {
		            System.out.println(numero);
		            numero += 2;
		        } while (numero <= 50);
	}

}
